package in.scms;


public class LoginDTO {

	private String username;
	private String password;
	private String hiddenVariable;
	
	
	public LoginDTO() {
		super();
		// TODO Auto-generated constructor stub
	}
	public LoginDTO(String username, String password, String hiddenVariable) {
		super();
		this.username = username;
		this.password = password;
		this.hiddenVariable = hiddenVariable;
	}
	public String getUsername() {
		return username;
	}
	public void setUsername(String username) {
		this.username = username;
	}
	public String getPassword() {
		return password;
	}
	public void setPassword(String password) {
		this.password = password;
	}
	public String getHiddenVariable() {
		return hiddenVariable;
	}
	public void setHiddenVariable(String hiddenVariable) {
		this.hiddenVariable = hiddenVariable;
	}
	@Override
	public String toString() {
		return "LoginDTO [username=" + username + ", password=" + password + ", hiddenVariable=" + hiddenVariable + "]";
	}
	
}
